public class FindAllAnagramsinString {

    public List<Integer> findAnagrams(String s, String p) {
        int[] chars = new int[26];
        List<Integer> result = new ArrayList<>();

        if (s == null || p == null || s.length() < p.length())
            return result;
        for (char c : p.toCharArray())
            chars[c - 'a']++;

        int start = 0, end = 0;

        // count是 p中字符和s中字符相差的个数，当count=0，表示当前s中遍历的字符和p是同构的
        int count = p.length();
        // 遍历字符串s
        while (end < s.length()) {
            // 当前遍历长度为 p字符串的长度时
            if (end - start == p.length()) {
                char sstart=s.charAt(start);
                //chars[sstart - 'a']>0
                //  表示在当前已经遍历的结果中 p中的字符在s中存在
                // start 是表示窗口左边
                if (chars[sstart - 'a'] >= 0) {
                    count++;
                }
                chars[sstart - 'a']++;
                start++;
            }
            // 窗口的右边
            char send=s.charAt(end);
            chars[send - 'a']--;
            // p中的字符在s中存在
            if (chars[send - 'a'] >= 0) {
                count--;
            }
            end++;
            if (count == 0) result.add(start);

         /*   // If the char at start appeared in p, we increase count
            if (end - start == p.length() && chars[s.charAt(start++)-'a']++ >= 0)
                count++;
            // If the char at end appeared in p (since it's not -1 after decreasing), we decrease count
            if (--chars[s.charAt(end++)-'a'] >= 0)
                count--;
            if (count == 0)
                result.add(start);*/
        }

        return result;
    }


    public static void main(String args[]) {
        FindAllAnagramsinString f = new FindAllAnagramsinString();

        String s = "cbaebabacd";
        String p = "abc";
        System.out.println(f.findAnagrams(s, p));
    }
}
